import React from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faEdit, faMinusCircle } from '@fortawesome/free-solid-svg-icons'
import { faGithub } from '@fortawesome/free-brands-svg-icons'
import { DevForm } from '../DevForm/index';

import './styles.css';

function DevItem({ dev }) {

  return (
    <li className="dev-item">
      <header>
        <img src={dev.avatar_url} alt={dev.name} />
        <div className="user-info">
          <strong>{dev.name}</strong>
          <span>{dev.techs.join(', ')}</span>
        </div>
      </header>
      <p>{dev.bio}</p>
      <div className="app-states">
        <a href={`https://github.com/${dev.github_username}`}><FontAwesomeIcon icon={faGithub} size="2x" color="blue" /></a>
        <a href={`https://github.com/${dev.github_username}`}><FontAwesomeIcon icon={faEdit} size="2x" color="green" /></a>
        <a href={`https://github.com/${dev.github_username}`}><FontAwesomeIcon icon={faMinusCircle} size="2x" color="red  " /></a>

        <button key={dev.github_username} value={dev.github_username} onClick={() => DevForm.getData(dev.github_username)}>
          <FontAwesomeIcon size="2x" icon={faEdit} />
        </button>
      </div>

    </li>
  );
}

export default DevItem;
